import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card ,Container, Row,Col,Button,CardGroup} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faAngleLeft,faAngleRight} from '@fortawesome/free-solid-svg-icons'

import '../css/offer.css'
const Offer = () => {
    let offers=[
    {
       image:require('../images/dubai.jpeg'),
       title:"Dubai. United Arab Emirates"
    },
    {
        image:require('../images/dubai.jpeg'),
        title:"Dubai. United Arab Emirates"
     },
     {
        image:require('../images/dubai.jpeg'),
        title:"Dubai. United Arab Emirates"
     }];
    function loop() {
        offers.array.forEach(offer => {
          <Card style={{ width: '21rem' }}>
          <Card.Img variant="top" src={offer.title} />
          <Card.Body>
              <Card.Title>{offer.image}</Card.Title>        
          </Card.Body>
          </Card>
        });

    }
  return (
    <Container>
    <div className='offer'>
     <Row className='align-items-center'>
        <Col className='title' lg={2}>
          <h2>Special <span>Offer</span></h2>
        </Col>
        <Col lg={9}><hr /></Col>
        <Col lg={1}>
        <Button className='radius me-2'><FontAwesomeIcon icon={faAngleLeft} /></Button>
        <Button className='radius'><FontAwesomeIcon icon={faAngleRight} /></Button>
        </Col>
     </Row>
       <CardGroup>

        {loop}
       </CardGroup>

    </div>
    </Container>

  )
}

export default Offer
